import os,sys,json,ssl,urllib.request,urllib.parse; d=os.getenv('TARGET_DOMAIN','example.com'); k=os.getenv('DNSDB_API_KEY'); 
if not k: print(json.dumps({'source':'dnsdb','query':d,'error':'missing DNSDB_API_KEY'}), file=sys.stderr); sys.exit(1); 
url='https://api.dnsdb.info/dnsdb/v2/lookup/rrset/name/'+urllib.parse.quote(d)+'?limit=50'; ctx=ssl.create_default_context(); hdr={'X-API-Key':k,'Accept':'application/json','User-Agent':'t1597-demo/1.0'}; 
try:
    r=urllib.request.urlopen(urllib.request.Request(url,headers=hdr),timeout=25,context=ctx); print(json.dumps({'source':'dnsdb','query':d,'data':json.loads(r.read().decode('utf-8'))}, ensure_ascii=False));
except Exception as e:
    print(json.dumps({'source':'dnsdb','query':d,'error':str(e)}), file=sys.stderr); sys.exit(2)
